import * as React from "react";
import { useState } from "react";
import PropTypes from "prop-types";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import CardActionArea from "@mui/material/CardActionArea";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";

export const Items = (props) => {
  const [art, setArt] = useState([]);

  // 初回レンダリングする場合の検索機能

  // useEffect(()=>{
  //     fetch('https://shopping.yahooapis.jp/ShoppingWebService/V3/itemSearch?appid=dj00aiZpPUZPU3h2a21KeG5pVSZzPWNvbnN1bWVyc2VjcmV0Jng9NDQ-&query=絵画&genre_category_id=11561&price_from=1000&price_to=5000',{mode:"cors"})
  //     .then(res=>res.json())
  //     .then(data=>setArt(data.hits))
  // },[])

  console.log(art);

  const handleDelete = (index) => {
    const deleteItem = art.filter((artItem) => artItem.index !== index);
    setArt(deleteItem);
  };

  const detail = art.map((item) => {
    return (
      <>
        <div>
          <p key={item.id}>{item.name}</p>
          <div>
            <img src={item.image.medium} />
          </div>
          <p>
            <a href={item.review.url}>レビュー</a>
          </p>
          <p>
            <a href={item.url}>詳細/お求めはこちら</a>
          </p>
          <button onClick={() => handleDelete(item.index)}>DELETE</button>
        </div>
      </>
    );
  });

  // 検索で入力できるようにするためのsetState
  const [search, setSearch] = useState("");

  // 送信機能
  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(
      `https://shopping.yahooapis.jp/ShoppingWebService/V3/itemSearch?appid=dj00aiZpPUZPU3h2a21KeG5pVSZzPWNvbnN1bWVyc2VjcmV0Jng9NDQ-&query=${search}&genre_category_id=11561&price_from=1000&price_to=5000&results=100`,
      { mode: "cors" }
    )
      .then((res) => res.json())
      .then((data) => setArt(data.hits));
    console.log("送信完了しました");
  };

  // useEffect(()=>{
  //     console.log(detail[1].props.children[0].props.children)
  // },[art])
  // const { post } = props;

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          onChange={(e) => setSearch(e.target.value)}
          value={search}
        />
        <button type="submit">SEARCH</button>
      </form>

      {detail}

      <Grid item xs={12} md={6}>
        <CardActionArea component="a" href="#">
          <Card sx={{ display: "flex" }}>
            <CardContent sx={{ flex: 1 }}>
              <Typography component="h2" variant="h5">
                {post.title}
              </Typography>
              <Typography variant="subtitle1" color="text.secondary">
                {post.date}
              </Typography>
              <Typography variant="subtitle1" paragraph>
                {post.description}
              </Typography>
              <Typography variant="subtitle1" color="primary">
                Continue reading...
              </Typography>
            </CardContent>
            <CardMedia
              component="img"
              sx={{ width: 160, display: { xs: "none", sm: "block" } }}
              image={post.image}
              alt={post.imageLabel}
            />
          </Card>
        </CardActionArea>
      </Grid>
    </>
  );
};
