import React,{useState, useEffect} from 'react'

export const Items =()=>{

    const [art, setArt]=useState([])

    // 初回レンダリングする場合の検索機能

    // useEffect(()=>{
    //     fetch('https://shopping.yahooapis.jp/ShoppingWebService/V3/itemSearch?appid=dj00aiZpPUZPU3h2a21KeG5pVSZzPWNvbnN1bWVyc2VjcmV0Jng9NDQ-&query=絵画&genre_category_id=11561&price_from=1000&price_to=5000',{mode:"cors"})
    //     .then(res=>res.json())
    //     .then(data=>setArt(data.hits))
    // },[])

    console.log(art)

    const handleDelete=id=>{
        const deleteItem=art.filter(artItem=>artItem.id !== id)
        setArt(deleteItem)
    }

    const detail = art.map((item)=>{
        return(
            <>
            <div >
                <p　key={item.index}>{item.name}</p>
                <div>
                    <img src={item.image.medium}/>
                </div>
                <p><a href={item.review.url}>レビュー</a></p>
                <p><a href={item.url}>詳細/お求めはこちら</a></p>
                <button onClick={()=>handleDelete(item.index)}>DELETE</button>
            </div>
            </>
        )
    })


    // 検索で入力できるようにするためのsetState
    const [search, setSearch]=useState('')

    // 送信機能
    const handleSubmit=e=>{
        e.preventDefault()
        fetch(`https://shopping.yahooapis.jp/ShoppingWebService/V3/itemSearch?appid=dj00aiZpPUZPU3h2a21KeG5pVSZzPWNvbnN1bWVyc2VjcmV0Jng9NDQ-&query=${search}&genre_category_id=11561&price_from=1000&price_to=5000&results=100`,{mode:"cors"})
        .then(res=>res.json())
        .then(data=>setArt(data.hits))
        console.log('送信完了しました')
    }

    // useEffect(()=>{
    //     console.log(detail[1].props.children[0].props.children)
    // },[art])

    return(
        <>
        <form onSubmit={handleSubmit}>
            <input
                type='text'
                onChange={e=>setSearch(e.target.value)}
                value={search}
            />
            <button type='submit'>SEARCH</button>
        </form>

        {detail}
        </>
    )}
