import * as React from "react";
import PropTypes from "prop-types";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import CardActionArea from "@mui/material/CardActionArea";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";

export const Items2 = () => {
  const [art, setArt] = useState([]);

  // 検索で入力できるようにするためのsetState
  const [search, setSearch] = useState("");

  // 送信機能
  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(
      `https://shopping.yahooapis.jp/ShoppingWebService/V3/itemSearch?appid=dj00aiZpPUZPU3h2a21KeG5pVSZzPWNvbnN1bWVyc2VjcmV0Jng9NDQ-&query=${search}&genre_category_id=11561&price_from=1000&price_to=5000&results=100`,
      { mode: "cors" }
    )
      .then((res) => res.json())
      .then((data) => setArt(data.hits));
    console.log("送信完了しました");
  };

  <Grid item xs={12} md={6}>
    <CardActionArea component="a" href="#">
      <Card sx={{ display: "flex" }}>
        <CardContent sx={{ flex: 1 }}>
          <Typography component="h2" variant="h5">
            {post.title}
          </Typography>
          <Typography variant="subtitle1" color="text.secondary">
            {post.date}
          </Typography>
          <Typography variant="subtitle1" paragraph>
            {post.description}
          </Typography>
          <Typography variant="subtitle1" color="primary">
            Continue reading...
          </Typography>
        </CardContent>
        <CardMedia
          component="img"
          sx={{ width: 160, display: { xs: "none", sm: "block" } }}
          image={post.image}
          alt={post.imageLabel}
        />
      </Card>
    </CardActionArea>
  </Grid>;

  return <></>;
};

Items2.propTypes = {
  post: PropTypes.shape({
    date: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    image: PropTypes.string.isRequired,
    imageLabel: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
  }).isRequired,
};
