import React,{useState, useEffect} from 'react'

export const Items =()=>{

    const [art, setArt]=useState([])

    useEffect(()=>{
        fetch('https://shopping.yahooapis.jp/ShoppingWebService/V3/itemSearch?appid=dj00aiZpPUZPU3h2a21KeG5pVSZzPWNvbnN1bWVyc2VjcmV0Jng9NDQ-&query=西洋絵画&genre_category_id=11561&price_from=1000&price_to=5000',{mode:"cors"})
        .then(res=>res.json())
        .then(data=>setArt(data.hits))
    },[])

    console.log(art)

    const name = art.map((item,id)=>{
        return(
            <div key={id}>
                <p>{item.name}</p>
                <div>
                    <img src={item.image.medium}/>
                </div>
                <p><a href={item.review.url}>レビュー</a></p>
                <p><a href={item.url}>詳細/お求めはこちら</a></p>
            </div>
        )
    })

    // 検索で入力できるようにするためのsetState
    const [search, setSearch]=useState('')

    // 送信機能
    const handleSubmit=e=>{
        e.preventDefault()
        console.log('送信完了しました')
    }

    return(
        <>
        <form onSubmit={handleSubmit}>
            <input
                type='text'
                onChange={e=>setSearch(e.target.value)}
                value={search}
            />
            <button type='submit'>SEARCH</button>
        </form>

        {name}
        </>
    )}
