// SEARCH.JS(完全一致・filterで勝手に検索してくれる＝検索ボタンとか押さなくていい)


import React,{useState, useEffect} from 'react'

export const App=(props)=>{

  const fruits = props.itemsJson

  // onSubmitが走った時にfilterが走る

  const[text, setText]=useState('')
  const[result, setResult]=useState('')
  const onChange =e=>{
    setText(e.target.value)
  }

  const handleSearch =e=>{
    e.preventDefault();
    const fruitFilter=fruits.filter(fruit=>{
        return fruit===text
      }
    );
    setResult(fruitFilter)
    // ↑fruitとtextが同じだった場合の答え(returnの答え)をsetResultに入れてonSubmitが入ったときに変更する
  }

  // const onSubmit=e=>{

  //   setText(text)
  //   console.log('onSubmitが適用されました')
  // }


  return (
    <>
    <form onSubmit={handleSearch}>
      <input
        value={text}
        type='text'
        onChange={onChange}
      />

      <button type='submit'>Search</button>
      <p>{result}</p>
    </form>



{/* 
    ↑フォーム内と結果表示画面を繋ぐには（この二つに限らず何かを繋ぐ場合は）、何かインターフェースとなるものが必要。
    今回はtextというインターフェースで繋いだ。 */}

    </>
  )
}


export default App;


