import React,{useState} from 'react'
import ItemsJson from './television.json'

export const Items =()=>{

    console.log(ItemsJson)

    const[search, setSearch]=useState('')
    const handleSearch=e=>e.target.value

    // const itemName=()=>{
    //     return(ItemsJson.name.filter(item=>{
    //         return item===item.name
    //     }))
    // }
    // console.log(itemName)


    return(
        <>
            <input
            type='text'
            value={search}
            onChange={handleSearch}
            />

            {ItemsJson.map((item, id)=>{
                return(
                    <div key={id}>
                    <img src={item.image.medium}/>
                    <p>{item.name}</p>
                    <button>
                        <a href={item.url}>詳細へ</a>
                    </button>
                    <button>
                        <a href={item.review.url}>レビューを見る</a>
                    </button>
                    </div>
                )
            })}
        </>
    )
}
